import React, { useRef, useState } from "react";

export default React.forwardRef(function Contact(props, ref) {

    let [name, setName] = useState('')
    let [nameError, setNameError] = useState('')
    let [phone, setPhone] = useState('')
    let [email, setEmail] = useState('')
    let [website, setWebsite] = useState('')
    let [title, setTitle] = useState('')
    let [content, setContent] = useState('')

    let inputRef = useRef(null)

    function _btnFocus() {
        inputRef.current.focus();
    }


    function btnResgister() {
        if (name === '') {
            setNameError('ho va ten khong duoc de trong')

        }
        else {
            setNameError('')
        }
    }


    return (
        <main className="register-course" id="main">
            <section className="section-1 wrap container">
                {/* <div class="main-sub-title">liên hệ</div> */}
                <h2 className="main-title">HỢP TÁC CÙNG CFD</h2>
                <p className="top-des">
                    Đừng ngần ngại liên hệ với <strong>CFD</strong> để cùng nhau tạo ra những sản phẩm giá trị, cũng như
            việc hợp tác với các đối tác tuyển dụng và công ty trong và ngoài nước.
          </p>
                {/* <button onClick={_btnFocus}>Focus</button> */}
                <div className="form">
                    <label>
                        <p>Họ và tên<span>*</span></p>
                        <input value={name} onChange={(e) => setName(e.target.value)} type="text" placeholder="Họ và tên bạn" />
                        {nameError ? <p className="error-text">{nameError}</p> : null}
                    </label>
                    <label>
                        <p>Số điện thoại</p>
                        <input value={phone} onChange={(e) => setPhone(e.target.value)} type="text" placeholder="Số điện thoại" />
                    </label>
                    <label>
                        <p>Email<span>*</span></p>
                        <input value={email} onChange={(e) => setEmail(e.target.value)} type="text" placeholder="Email của bạn" />
                    </label>
                    <label>
                        <p>Website</p>
                        <input value={website} onChange={(e) => setWebsite(e.target.value)} type="text" placeholder="Đường dẫn website http://" />
                    </label>
                    <label>
                        <p>Tiêu đề<span>*</span></p>
                        <input value={title} onChange={(e) => setTitle(e.target.value)} type="text" placeholder="Tiêu đề liên hệ" />
                    </label>
                    <label>
                        <p>Nội dung<span>*</span></p>
                        <textarea value={content} onChange={(e) => setContent(e.target.value)} name id cols={30} rows={10} defaultValue={""} />
                    </label>
                    <div className="btn main rect" onClick={btnResgister}>đăng ký</div>
                </div>
            </section>
            {/* <div class="register-success">
            <div class="contain">
                <div class="main-title">đăng ký thành công</div>
                <p>
                    <strong>Chào mừng Trần Nghĩa đã trở thành thành viên mới của CFD Team.</strong> <br>
                    Cảm ơn bạn đã đăng ký khóa học tại <strong>CFD</strong>, chúng tôi sẽ chủ động liên lạc với bạn thông qua facebook
                    hoặc số điện thoại của bạn.
                </p>
            </div>
            <a href="/" class="btn main rect">về trang chủ</a>
        </div> */}
        </main>
    )
})
