        document.body.classList.remove('menu-is-show')
        document.querySelector('.loading-page').style.transform = 'scale(25)'
        e.preventDefault();
        setTimeout(() => {
            history.push(e.target.href?.replace(window.location.origin, '') || "")
            document.querySelector('.loading-page').style.transform = 'scale(0)'
        }, 1000)

    }

    if (!store) {
        let store = createStore(reducer, composeEnhancers(applyMiddleware(thunkFake)))

    }



    return (
        <Provider store={store}>
            <Context.Provider value={{ linkLoading }}>
                {children}
            </Context.Provider>
        </Provider>
    );
}
export default function AppProvider({ children, store }) {
    return <BrowserRouter>
        <App store={store}>
            {children}
        </App>
    </BrowserRouter>
}
