import { AUTH_LOGIN, AUTH_POPUP } from "../actions/type";

let initialState = JSON.parse(localStorage.getItem('auth')) || {
    login: false,
    user: null,
    popupOpen: false
}

export default function authReducer(state = initialState, active) {
    switch (active.type) {
        case AUTH_POPUP:
            return {
                ...state,
                popupOpen: active.payload
            }

        case AUTH_LOGIN:

            localStorage.setItem('auth', JSON.stringify(active.payload))
            if (active.payload.token) {
                localStorage.setItem('token', JSON.stringify(active.payload.token))
            }
            return {
                ...state,
                user: active.payload
            }

        default:
            return state;
    }

}
