let $ = window.$

export default function Accordion({ num, title, content }) {


    let ref = useRef()
    function _onClick(e) {
        let $parent = $(ref.current).closest('.accordion')
        $parent.toggleClass('active')
        $(e.currentTarget).next().slideToggle(200)
        if ($parent.hasClass('active')) {
            $('.accordion.active').not($parent).removeClass('active')
                .find('.content').slideUp(200)
        }

    }
    return (
        <div className="accordion">
            <div className="accordion__title" ref={ref} onClick={_onClick}>
                <div className="date">Ng√†y {num}</div>
                <h3>{title}</h3>
            </div>
            <div className="content">
                {content}
            </div>
        </div>
    )
}
