import React, { useContext, useRef, useState } from 'react';
import {
  BrowserRouter,
  Route,
  Switch
} from 'react-router-dom'

import './assets/css/custom.css';

import renderRouters from './core/routersConfig';
import routers from './routers';

export let Context = React.createContext()

function App() {
  let [auth, setAuth] = useState({
    login: false,
    user: null
  })


  function handleLogin() {
    setAuth({
      login: true,
      user: {
        name: "Đặng Thuyền Vương"
      }
    })
  }
  function logout() {
    setAuth({
      login: false,
      user: null
    })
  }

  let history = useHistory()

  function openMenu() {
    document.body.classList.toggle('menu-is-show')

  }
  function linkLoading(e) {
    document.body.classList.remove('menu-is-show')
    document.querySelector('.loading-page').style.transform = 'scale(25)'
    e.preventDefault();
    setTimeout(() => {
      history.push(e.target.href.replace(window.location.origin, ''))
      document.querySelector('.loading-page').style.transform = 'scale(0)'
    }, 1000)

  }



  return (
    <Context.Provider value={{ ...auth, handleLogin, logout, linkLoading }}>
      <BrowserRouter>
        {renderRouters(routers)}
      </BrowserRouter>
    </Context.Provider>

  );
}

export default App;
