import { AUTH_LOGIN, AUTH_LOGOUT, AUTH_POPUP } from "./type";

export function popupLogin(flag) {
    return {
        type: AUTH_POPUP,
        payload: flag
    }
}

export function login(user) {
    return {
        type: AUTH_LOGIN,
        payload: user
    }

}

export function logout() {
    return {
        type: AUTH_LOGOUT
    }
}

export function updateInfo(user) {
    return {
        type: AUTH_UPDATEINFO,
        payload: user
    }

}
