let initialState = {
    count: 0
}

export default function authReducer(state = initialState, action) {
    // console.log(action, state)
    switch (action.type) {
        case 'INCREMENT':
            return {
                ...state,
                count: state.count + action.payload || 1
            }
        case 'DECREMENT':
            return {
                ...state,
                count: state.count - action.payload || 1
            }
        default:
            return state;
    }
}
