import { createStore } from "redux";

let initialState = {
    count: 0
}

function authReducer(state = initialState, action) {
    switch (action.type) {
        case 'INCREMENT':
            return {
                ...state,
                count: state.count + 1
            }
        case 'DECREMENT':
            return {
                ...state,
                count: state.count - 1
            }
        default:
            return state;
    }



}
let store = createStore(authReducer)

export default store
