import { login } from "../actions/authAction";
import { AUTH_LOGIN, AUTH_LOGOUT, AUTH_POPUP, AUTH_UPDATEINFO } from "../actions/type";

let initialState = JSON.parse(localStorage.getItem('auth')) || {
    login: false,
    user: null,
    popupOpen: false
}

export default function authReducer(state = initialState, active) {
    switch (active.type) {
        case AUTH_POPUP:
            return {
                ...state,
                popupOpen: active.payload
            }

        case AUTH_LOGIN:


            // localStorage.setItem('auth', JSON.stringify({
            //     user: active.payload,
            //     login: true
            // }))
            if (active.payload.token) {
                localStorage.setItem('token', JSON.stringify(active.payload.token))
            }
            return {
                ...state,
                login: true,
                user: active.payload
            }


        case AUTH_LOGOUT:
            localStorage.removeItem('auth')
            localStorage.removeItem('token')
            return {
                ...state,
                login: false,
                user: null
            }
        case AUTH_UPDATEINFO:
            return {
                ...state,
                user: active.payload
            }
        default:
            return state;
    }

}
