export default function createSlice({ initialState, reducers, name }) {
    let action = {}

    let type = {}

    for (let i in reducers) {
        let t = `${name}/${i}`
        console.log(type);
        action[i] = (data) => ({
            type: t,
            payload: data
        })


        type[i] = t
    }

    function reducer(state = initialState, action) {
        let t = action.type.split('/')?.[1]

        if (t in reducers) {
            return reducers[t](state, action);
        }

        return state
    }

    return {
        action,
        type,
        reducer
    }
}
