import React, { useState } from "react";
import { BrowserRouter, useHistory } from "react-router-dom";




export let Context = React.createContext()

function Provider({ children }) {
    let [auth, setAuth] = useState({
        login: false,
        user: null
    })


    function handleLogin() {
        setAuth({
            login: true,
            user: {
                name: "Đặng Thuyền Vương"
            }
        })
    }
    function logout() {
        setAuth({
            login: false,
            user: null
        })
    }
    let history = useHistory()

    function linkLoading(e) {
        document.body.classList.remove('menu-is-show')
        document.querySelector('.loading-page').style.transform = 'scale(25)'
        e.preventDefault();
        setTimeout(() => {
            history.push(e.target.href.replace(window.location.origin, '') || '')
            document.querySelector('.loading-page').style.transform = 'scale(0)'
        }, 1000)

    }



    return (
        <Context.Provider value={{ ...auth, handleLogin, logout, linkLoading }}>
            {children}
        </Context.Provider>
    );
}

export default function AppProvider() {
    return <BrowserRouter>
        <Provider>

        </Provider>
    </BrowserRouter>
}
