import React, { useEffect, useState } from "react";
import { BrowserRouter, useHistory } from "react-router-dom";
import userApi from "../api/userApi";




export let Context = React.createContext()


function Provider({ children }) {

    let [auth, setAuth] = useState(JSON.parse(localStorage.getItem('auth')) || {
        login: false,
        user: null,
        token: ""
    })
    let [token, setToken] = useState(JSON.parse(localStorage.getItem('token')) || {
        token: ""
    })


    useEffect(() => {
        if (auth.login) {
            localStorage.setItem('auth', JSON.stringify(auth))
            localStorage.setItem('token', JSON.stringify(auth.token))
        } else {
            localStorage.removeItem('auth')
            localStorage.removeItem('token')
        }

        // localStorage.setItem('token', JSON.stringify(token))
    }, [auth])



    async function handleLogin(form) {

        let res = await userApi.login(form)
        if (res.error) {
            return res
        } else {
            setAuth({
                login: true,
                user: res.data,

            })
            // setToken({
            //     token: res.data.token
            // })
        }

    }

    function logout() {
        setAuth({
            login: false,
            user: null
        })
    }
    let history = useHistory()

    function linkLoading(e) {
        console.log(e.target.href);
        document.body.classList.remove('menu-is-show')
        document.querySelector('.loading-page').style.transform = 'scale(25)'
        e.preventDefault();
        setTimeout(() => {
            history.push(e.target.href?.replace(window.location.origin, '') || "")
            document.querySelector('.loading-page').style.transform = 'scale(0)'
        }, 1000)

    }

    function popupLogin(flag = true) {
        document.getElementById('popupLogin').style.display = flag ? "flex" : "none"
    }


    return (
        <Context.Provider value={{ ...auth, handleLogin, logout, linkLoading, popupLogin }}>
            {children}
        </Context.Provider>
    );
}
export default function AppProvider({ children }) {
    return <BrowserRouter>
        <Provider>
            {children}
        </Provider>
    </BrowserRouter>
}
