import React, { useRef } from "react"



export default function Accordion({ num, title, content }) {

    let ref = useRef()

    function _onClick(e) {
        let parent = $(e.currentTarger).closest('.accordion')
        parent.toggleClass('active')
        $(e.currentTarger).next().slideToggle(200)
        if (parent.hasClass('active')) {
            $('.accordion').not(parent).removeClass('active')
                .find('.content').slideUp(200)
        }
    }
    return (
        <div className="accordion">
            <div className="accordion__title" onClick={_onClick}>
                <div className="date">Ng√†y {num}</div>
                <h3>{title}</h3>
            </div>
            <div className="content">
                {content}
            </div>
        </div>
    )
}
