import useFormValidate from "../../../core/useFormValidate"

export default function Tab1() {
    let { form, error, inputChange, check } = useFormValidate({
        name: '',
        phone: '',
        email: 'Dangthuyenvuong@gmail.com',
        facebook: '',
        skype: '',

    }, {
        rule: {
            name: {
                required: true,
            },
            phone: {
                required: true,
                pattern: "phone",
            },
            facebook: {
                required: true,
                pattern: "facebook"
            },
            skype: {
                required: true,

            }
        },
        message: {
            name: {
                required: "Họ tên không được để trống"
            },
            phone: {
                required: "Phone không được để trống",
                pattern: "Sai số điện thoại"
            },
            email: {
                required: "Email không được để trống",
                pattern: "Email không đúng định dạng"

            },
            facebook: {
                required: "Facebook không được để trống",
                pattern: "Facebook không đúng định dạng"
            }
        }, options: {
            localStorage: "profile-info"
        }
    })

    function _save() {
        let error = check();

        if (Object.keys(error).length === 0) {
            alert("thành công")
        }
    }
    return (
        <>
            <div className="top-info">
                <div className="avatar">
                    {/* <span class="text">H</span> */}
                    <img src="img/avatar-lg.png" alt="" />
                    <div className="camera" />
                </div>
                <div className="name">{user.name}</div>
                <p className="des">Thành viên của team CFD1-OFFLINE</p>
            </div>
            <div className="tab1">
                <label>
                    <p>Họ và tên<span>*</span></p>
                    <input onChange={inputChange} name="name" value={form.name} type="text" placeholder="Nguyễn Văn A" />
                    {error.name && <p className="error-text">{error.name}</p>}
                </label>
                <label>
                    <p>Số điện thoại<span>*</span></p>
                    <input onChange={inputChange} name="phone" value={form.phone} type="text" placeholder="0949******" />
                    {error.phone && <p className="error-text">{error.phone}</p>}
                </label>
                <label>
                    <p>Email<span>*</span></p>
                    <input onChange={inputChange} name="email" value={form.email} disabled type="text" />
                    {error.email && <p className="error-text">{error.email}</p>}
                </label>
                <label>
                    <p>Facebook<span>*</span></p>
                    <input onChange={inputChange} name="facebook" value={form.facebook} type="text" placeholder="Facebook url" />
                    {error.facebook && <p className="error-text">{error.facebook}</p>}
                </label>
                <label>
                    <p>Skype<span>*</span></p>
                    <input onChange={inputChange} name="skype" value={form.skype} type="text" placeholder="Skype url" />
                    {error.skype && <p className="error-text">{error.skype}</p>}
                </label>
                <div className="btn main rect" onClick={_save}>LƯU LẠI</div>
            </div >
        </>
    )
}
