import React, { forwardRef, useImperativeHandle, useState } from 'react'
import reactDom from 'react-dom'
import useAuth from '../core/useAuth'
import useFormValidate from '../core/useFormValidate'

export default forwardRef(function Login(props, ref) {

    let { handleLogin } = useAuth()

    let { form, error, inputChange, check } = useFormValidate({
        username: '',
        password: ''
    }, {
        rule: {
            username: {
                required: true,
                pattern: "email"
            },
            password: {
                required: true
            }
        },
        message: {
            username: {
                pattern: "Email không đúng định dạng"
            }
        }
    })





    let [isOpen, setIsOpen] = useState(false)

    useImperativeHandle(ref, () => {
        return {
            open,
            close
        }
    }, [])



    function open() {
        setIsOpen(true)
    }

    function close() {
        setIsOpen(false)
    }

    function _login() {
        let error = check();

        if (Object.keys(error).length === 0) {
            handleLogin(form)
        }
    }


    return reactDom.createPortal(
        <div className="popup-form popup-login" id='popupLogin' style={{ display: isOpen ? 'flex' : 'none' }}>
            <div className="wrap">
                {/* login-form */}
                <div className="ct_login" style={{ display: 'block' }}>
                    <h2 className="title">Đăng nhập</h2>
                    <label>
                        <input name='username' value={form.username} onChange={inputChange} type="text" placeholder="Email / Số điện thoại" />
                        {error.username && <p className="error-text errorLogin">{error.username}</p>}
                    </label>
                    <label>
                        <input name='password' value={form.password} onChange={inputChange} type="password" placeholder="Mật khẩu" />
                        {error.password && <p className="error-text errorLogin">{error.password}</p>}
                    </label>


                    <div className="remember">
                        <label className="btn-remember">
                            <div>
                                <input type="checkbox" />
                            </div>
                            <p>Nhớ mật khẩu</p>
                        </label>
                        <a href="#" className="forget">Quên mật khẩu?</a>
                    </div>
                    <div className="btn rect main btn-login" onClick={_login}>đăng nhập</div>
                    <div className="text-register" style={{}}>
                        <strong>hoặc đăng ký bằng</strong>
                    </div>
                    <div>
                        <div className="btn btn-icon rect white btn-google">
                            <img src="img/google.svg" alt />
          Google
        </div>
                    </div>
                    <div className="close">
                        <img src="img/close-icon.png" alt />
                    </div>
                </div>
                {/* email form */}
                <div className="ct_email">
                    <h2 className="title">Đặt lại mật khẩu</h2>
                    <input type="text" placeholder="Email" />
                    <div className="btn rect main btn-next">Tiếp theo</div>
                    <div className="back" />
                    <div className="close">
                        <img src="img/close-icon.png" alt />
                    </div>
                </div>
            </div>
        </div>,
        document.getElementById('root2')
    )
})
