export default {
    login: (data) => {
        return fetch('http://cfd-reactjs.herokuapp.com/elearning/v4/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        }).then(res => res.json())
    }
}
